import * as express from 'express';
import 'express-async-errors';
import ErrorMiddleware from './middlewares/ErrorMiddleware';
import router from './router';

class App {
  public app: express.Express;

  constructor(private routes: express.Router = router) {
    this.app = express();

    this.config();

    // Não remover essa rota
    this.app.get('/', (req, res) => res.json({ ok: true }));
    this.app.use(this.routes);
    this.app.use(ErrorMiddleware.handleError);
  }

  private config():void {
    const accessControl: express.RequestHandler = (_req, res, next) => {
      res.header('Access-Control-Allow-Origin', '*');
      res.header('Access-Control-Allow-Methods', 'GET,POST,DELETE,OPTIONS,PUT,PATCH');
      res.header('Access-Control-Allow-Headers', '*');
      next();
    };

    this.app.use(express.json());
    this.app.use(accessControl);
  }

  public start(PORT: string | number):void {
    this.app.listen(PORT, () => console.log(`Running on port ${PORT}`));
  }
}

export { App };

// Essa segunda exportação é estratégica, e a execução dos testes de cobertura depende dela
export const { app } = new App();

import { NextFunction, Request, Response } from 'express';
import ValidateError400 from '../errors/ValidateError400';
import ValidateError401 from '../errors/ValidateError401';
import ValidateError404 from '../errors/ValidateError404';
import ValidateError422 from '../errors/ValidateError422';

export default class ErrorMiddleware {
  static handleError(error: Error, req: Request, res: Response, _next: NextFunction) {
    if (error instanceof ValidateError400) {
      return res.status(400).json({ message: error.message });
    }

    if (error instanceof ValidateError401) {
      return res.status(401).json({ message: error.message });
    }

    if (error instanceof ValidateError404) {
      return res.status(404).json({ message: error.message });
    }

    if (error instanceof ValidateError422) {
      return res.status(422).json({ message: error.message });
    }

    console.error(error);
    res.status(500).json({ message: error.message });
  }
}

export default class ValidateError400 extends Error {
  name = 'ValidateError400';
}

export default class ValidateError401 extends Error {
  name = 'ValidateError401';
}

export default class ValidateError404 extends Error {
  name = 'ValidateError404';
}

export default class ValidateError422 extends Error {
  name = 'ValidateError422';
}

import { Router } from 'express';
import teamsRouter from './teams.router';
import loginRouter from './login.router';
import matchesRouter from './matches.router';
import leaderBoardRouter from './leaderBoard.router';

const router = Router();

router.use('/teams', teamsRouter);
router.use('/login', loginRouter);
router.use('/matches', matchesRouter);
router.use('/leaderboard', leaderBoardRouter);

export default router;

import { Router } from 'express';
import TeamsController from '../controller/TeamsController';

const teamsRouter = Router();

teamsRouter.get('/', (req, res) => TeamsController.getAll(req, res));
teamsRouter.get('/:id', (req, res) => TeamsController.getById(req, res));

export default teamsRouter;

import { Request, Response } from 'express';
import ValidateError400 from '../errors/ValidateError400';
import TeamsService from '../service/TeamsService';

export default class TeamsController {
  public static async getAll(req: Request, res: Response) {
    const allTeams = await TeamsService.getAll();
    res.status(200).json(allTeams);
  }

  public static async getById(req: Request, res: Response) {
    const { id } = req.params;
    const team = await TeamsService.getById(Number(id));
    if (typeof team === 'string') throw new ValidateError400('Team não encontrado');
    res.status(200).json(team);
  }
}

import TeamsModel, { teamAtributes } from '../database/models/TeamsModel';

export default class TeamsService {
  public static async getAll(): Promise<teamAtributes[]> {
    const teams = await TeamsModel.findAll();
    return teams;
  }

  public static async getById(id: number): Promise<teamAtributes | null | string> {
    const findTeam = await TeamsModel.findOne({ where: { id } });
    if (!findTeam) return 'Team não encontrado';
    return findTeam;
  }
}

import { Router } from 'express';
import ValidateLogin from '../middlewares/ValidateLogin';
import LoginController from '../controller/LoginController';
import AuthValidate from '../middlewares/AuthValidate';

const loginRouter = Router();

loginRouter.post(
  '/',
  (req, res, next) => ValidateLogin.valid(req, res, next),
  (req, res) => LoginController.login(req, res),
);

loginRouter.get(
  '/role',
  (req, res, next) => AuthValidate.tokenVerifiq(req, res, next),
  (req, res) => LoginController.role(req, res),
);

export default loginRouter;

import { NextFunction, Request, Response } from 'express';
import ValidateError401 from '../errors/ValidateError401';
import ValidateError400 from '../errors/ValidateError400';

export default class ValidateLogin {
  public static isValidEmail(email: string) {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return regex.test(email);
  }

  public static valid(req: Request, _res: Response, next: NextFunction) {
    const { email, password } = req.body;
    if (!email || !password) throw new ValidateError400('All fields must be filled');
    if (!ValidateLogin.isValidEmail(email) || password.length < 6) {
      throw new ValidateError401('Invalid email or password');
    }

    next();
  }
}

import { Request, Response } from 'express';
import { EmailAuth } from '../middlewares/AuthValidate';
import LoginService from '../service/LoginService';

export default class LoginController {
  public static async login(req: Request, res: Response) {
    const { email, password } = req.body;
    const token = await LoginService.login(email, password);
    res.status(200).json({ token });
  }

  public static async role(req: Request, res: Response) {
    const { email } = (req as EmailAuth).auth;
    const role = await LoginService.role(email);
    res.status(200).json(role);
  }
}

import { compareSync } from 'bcryptjs';
import ValidateError401 from '../errors/ValidateError401';
import UsersModel from '../database/models/UsersModel';
import authJwt from '../utils/authJwt';

export default class LoginService {
  public static async login(email: string, password: string) {
    const user = await UsersModel.findOne({ where: { email } });
    if (!user) throw new ValidateError401('Invalid email or password');
    if (!compareSync(password, user.password)) {
      throw new ValidateError401('Invalid email or password');
    }

    const token = authJwt.generateToken({ email, password });
    return token;
  }

  public static async role(email: string) {
    const user = await UsersModel.findOne({ where: { email } });
    if (!user) throw new ValidateError401('User not found');
    return { role: user.role };
  }
}

import * as jwt from 'jsonwebtoken';

const secretKey = process.env.JWT_SECRET as string;

type AppPayload = {
  email: string,
  password: string
};

const generateToken = (payload: AppPayload) => {
  const token = jwt.sign(payload, secretKey);
  return token;
};

const tokenVerify = (token: string): AppPayload => {
  const tokenDecoded = jwt.verify(token, secretKey);
  return tokenDecoded as AppPayload;
};

export default {
  generateToken,
  tokenVerify,
};

import { NextFunction, Request, Response } from 'express';
import ValidateError401 from '../errors/ValidateError401';
import authJwt from '../utils/authJwt';

export type EmailAuth = Request & {
  auth: {
    email: string
  }
};

export default class AuthValidate {
  public static tokenVerifiq(req: Request, _res: Response, next: NextFunction) {
    const { authorization } = req.headers;
    if (!authorization) {
      throw new ValidateError401('Token not found');
    }

    try {
      const decodedAuth = authJwt.tokenVerify(authorization);
      (req as EmailAuth).auth = { email: decodedAuth.email };
    } catch (error) {
      throw new ValidateError401('Token must be a valid token');
    }

    next();
  }
}

import { Router } from 'express';
import MatchesController from '../controller/MatchesController';
import AuthValidate from '../middlewares/AuthValidate';

const matchesRouter = Router();

matchesRouter.get('/', (req, res) => MatchesController.findAllMatchesWithTeams(req, res));

matchesRouter.patch(
  '/:id/finish',
  (req, res, next) => AuthValidate.tokenVerifiq(req, res, next),
  (req, res) => MatchesController.finishMatch(req, res),
);

matchesRouter.patch(
  '/:id',
  (req, res, next) => AuthValidate.tokenVerifiq(req, res, next),
  (req, res) => MatchesController.updateMatch(req, res),
);

matchesRouter.post(
  '/',
  (req, res, next) => AuthValidate.tokenVerifiq(req, res, next),
  (req, res) => MatchesController.createMatch(req, res),
);

export default matchesRouter;

import { Request, Response } from 'express';
import MatchesService from '../service/MatchesService';

export default class MatchesController {
  public static async findAllMatchesWithTeams(req: Request, res: Response) {
    const { inProgress } = req.query;
    if (typeof inProgress === 'string') {
      const filterMatches = await MatchesService.filterProgressQuery(inProgress);
      return res.status(200).json(filterMatches);
    }
    const allMatches = await MatchesService.findAllMatchesWithTeams();
    res.status(200).json(allMatches);
  }

  public static async finishMatch(req: Request, res: Response) {
    const { id } = req.params;
    const result = await MatchesService.finishMatch(Number(id));
    res.status(200).json(result);
  }

  public static async updateMatch(req: Request, res: Response) {
    const { id } = req.params;
    const { homeTeamGoals, awayTeamGoals } = req.body;
    const result = await MatchesService
      .updateMatch(Number(homeTeamGoals), Number(awayTeamGoals), Number(id));
    res.status(200).json(result);
  }

  public static async createMatch(req: Request, res: Response) {
    const matchEdited = await MatchesService.createMatch(req.body);
    res.status(201).json(matchEdited);
  }
}

import TeamsModel from '../database/models/TeamsModel';
import MatchesModel, { matchesAtributes } from '../database/models/MatchesModel';
import matchesInsert from '../interface/matchesInsert';
import TeamsService from './TeamsService';
import ValidateError422 from '../errors/ValidateError422';
import ValidateError404 from '../errors/ValidateError404';

export default class MatchesService {
  public static async findAllMatchesWithTeams(): Promise<matchesAtributes[]> {
    const matches = await MatchesModel.findAll({
      attributes: { exclude: ['home_team_id', 'away_team_id'] },
      include: [
        {
          model: TeamsModel,
          as: 'homeTeam',
          attributes: { exclude: ['id'] },
        },
        {
          model: TeamsModel,
          as: 'awayTeam',
          attributes: { exclude: ['id'] },
        },
      ],
    });

    return matches;
  }

  public static async findMatchesHomeById(id: number): Promise<matchesAtributes[]> {
    const matches = await MatchesModel.findAll({
      where: { homeTeamId: id, inProgress: false },
      attributes: { exclude: ['home_team_id', 'away_team_id'] },
      include: [
        {
          model: TeamsModel,
          as: 'homeTeam',
          attributes: { exclude: ['id'] },
        },
        {
          model: TeamsModel,
          as: 'awayTeam',
          attributes: { exclude: ['id'] },
        },
      ],
    });

    return matches;
  }

  public static async findMatchesAwayById(id: number): Promise<matchesAtributes[]> {
    const matches = await MatchesModel.findAll({
      where: { awayTeamId: id, inProgress: false },
      attributes: { exclude: ['home_team_id', 'away_team_id'] },
      include: [
        {
          model: TeamsModel,
          as: 'homeTeam',
          attributes: { exclude: ['id'] },
        },
        {
          model: TeamsModel,
          as: 'awayTeam',
          attributes: { exclude: ['id'] },
        },
      ],
    });

    return matches;
  }

  public static async filterProgressQuery(query: string) {
    const matchFilter = await MatchesModel.findAll({
      where: { inProgress: query === 'true' },
      attributes: { exclude: ['home_team_id', 'away_team_id'] },
      include: [
        {
          model: TeamsModel,
          as: 'homeTeam',
          attributes: { exclude: ['id'] },
        },
        {
          model: TeamsModel,
          as: 'awayTeam',
          attributes: { exclude: ['id'] },
        }],
    });
    return matchFilter;
  }

  public static async finishMatch(id: number) {
    await MatchesModel.update({ inProgress: false }, { where: { id } });
    return { message: 'finished' };
  }

  public static async updateMatch(homeTeamGoals: number, awayTeamGoals: number, id: number) {
    await MatchesModel.update({ homeTeamGoals, awayTeamGoals }, { where: { id } });
    return { message: 'match updated' };
  }

  public static async createMatch(matcheInsert: matchesInsert) {
    if (matcheInsert.awayTeamId === matcheInsert.homeTeamId) {
      throw new ValidateError422('It is not possible to create a match with two equal teams');
    }
    const idHome = await TeamsService.getById(matcheInsert.homeTeamId);
    const idAway = await TeamsService.getById(matcheInsert.awayTeamId);

    if (typeof idHome === 'string' || typeof idAway === 'string') {
      throw new ValidateError404('There is no team with such id!');
    }

    const newMatch = await MatchesModel.create({
      ...matcheInsert,
      inProgress: true,
    });
    return newMatch;
  }
}

import { Router } from 'express';
import LeaderBoardController from '../controller/LeaderBoardController';

const leaderBoardRouter = Router();

leaderBoardRouter.get(
  '/home',
  (req, res) => LeaderBoardController.findAllHomeLeaderBoard(req, res),
);

leaderBoardRouter.get(
  '/away',
  (req, res) => LeaderBoardController.findAllAwayLeaderBoard(req, res),
);

leaderBoardRouter.get(
  '/',
  (req, res) => LeaderBoardController.findAllGeneralLeaderBoard(req, res),
);

export default leaderBoardRouter;

import { Request, Response } from 'express';
import LeaderBoardService from '../service/LeaderBoardService';

export default class LeaderBoardController {
  public static async findAllHomeLeaderBoard(req: Request, res: Response) {
    const result = await LeaderBoardService.resultSort('home');
    res.status(200).json(result);
  }

  public static async findAllAwayLeaderBoard(req: Request, res: Response) {
    const result = await LeaderBoardService.resultSort('away');
    res.status(200).json(result);
  }

  public static async findAllGeneralLeaderBoard(req: Request, res: Response) {
    const homeLeader = await LeaderBoardService.resultSort('home');
    const awayLeader = await LeaderBoardService.resultSort('await');
    const result = await LeaderBoardService.findAllGeneral([...homeLeader, ...awayLeader]);
    res.status(200).json(result);
  }
}

import leaderBoard from '../interface/leaderBoard';
import { matchesAtributes } from '../database/models/MatchesModel';
import MatchesService from './MatchesService';
import TeamsService from './TeamsService';

export default class LeaderBoardService {
  public static async findAllLeaderBoard(endPoint: string): Promise<leaderBoard[]> {
    const teams = await TeamsService.getAll();
    const result = Promise.all(teams.map(async (team) => {
      const matches = endPoint === 'home' ? await MatchesService.findMatchesHomeById(team.id)
        : await MatchesService.findMatchesAwayById(team.id);
      return {
        name: team.teamName,
        totalPoints: this.pointsResult(matches, endPoint),
        totalGames: matches.length,
        totalVictories: this.sumVictorys(matches, endPoint),
        totalDraws: this.sumDrawns(matches),
        totalLosses: this.sumLosses(matches, endPoint),
        goalsFavor: this.sumGoalsFavor(matches, endPoint),
        goalsOwn: this.sumGoalsOwn(matches, endPoint),
        goalsBalance: this.resultGoalsBalance(matches, endPoint),
        efficiency: this.resultEfficiency(matches, endPoint),
      };
    }));

    return result;
  }

  public static async findAllGeneral(Leader: leaderBoard[]) {
    const teams = await TeamsService.getAll();
    const resultRedux = teams.map((team) => {
      const leaderRedux = Leader.filter((match) => match.name === team.teamName);
      return { name: team.teamName,
        totalPoints: leaderRedux[0].totalPoints + leaderRedux[1].totalPoints,
        totalGames: leaderRedux[0].totalGames + leaderRedux[1].totalGames,
        totalVictories: leaderRedux[0].totalVictories + leaderRedux[1].totalVictories,
        totalDraws: leaderRedux[0].totalDraws + leaderRedux[1].totalDraws,
        totalLosses: leaderRedux[0].totalLosses + leaderRedux[1].totalLosses,
        goalsFavor: leaderRedux[0].goalsFavor + leaderRedux[1].goalsFavor,
        goalsOwn: leaderRedux[0].goalsOwn + leaderRedux[1].goalsOwn,
        goalsBalance: leaderRedux[0].goalsBalance + leaderRedux[1].goalsBalance,
        efficiency: Number((((leaderRedux[0].totalPoints + leaderRedux[1].totalPoints)
        / ((leaderRedux[0].totalGames + leaderRedux[1].totalGames) * 3)) * 100).toFixed(2)),
      };
    });
    const result = await this.resultSort(resultRedux);
    return result;
  }

  public static pointsResult(matches: matchesAtributes[], endPoint: string) {
    if (endPoint === 'home') {
      const result = matches.reduce((acc, match) => {
        if (match.homeTeamGoals > match.awayTeamGoals) return acc + 3;
        if (match.homeTeamGoals < match.awayTeamGoals) return acc;

        return acc + 1;
      }, 0);
      return result;
    }
    const result = matches.reduce((acc, match) => {
      if (match.awayTeamGoals > match.homeTeamGoals) return acc + 3;

      if (match.awayTeamGoals < match.homeTeamGoals) return acc;

      return acc + 1;
    }, 0);
    return result;
  }

  public static sumVictorys(matches: matchesAtributes[], endPoint: string) {
    if (endPoint === 'home') {
      const result = matches.reduce((acc, match) =>
        (match.homeTeamGoals > match.awayTeamGoals ? acc + 1 : acc), 0);
      return result;
    }

    const result = matches.reduce((acc, match) =>
      (match.awayTeamGoals > match.homeTeamGoals ? acc + 1 : acc), 0);
    return result;
  }

  public static sumLosses(matches: matchesAtributes[], endPoint: string) {
    if (endPoint === 'home') {
      const result = matches.reduce((acc, match) =>
        (match.homeTeamGoals < match.awayTeamGoals ? acc + 1 : acc), 0);
      return result;
    }
    const result = matches.reduce((acc, match) =>
      (match.awayTeamGoals < match.homeTeamGoals ? acc + 1 : acc), 0);
    return result;
  }

  public static sumDrawns(matches: matchesAtributes[]) {
    const result = matches.reduce((acc, match) =>
      (match.homeTeamGoals === match.awayTeamGoals ? acc + 1 : acc), 0);
    return result;
  }

  public static sumGoalsFavor(matches: matchesAtributes[], endPoint: string) {
    if (endPoint === 'home') {
      const result = matches.reduce((acc, match) => acc + match.homeTeamGoals, 0);
      return result;
    }
    const result = matches.reduce((acc, match) => acc + match.awayTeamGoals, 0);
    return result;
  }

  public static sumGoalsOwn(matches: matchesAtributes[], endPoint: string) {
    if (endPoint === 'home') {
      const result = matches.reduce((acc, match) => acc + match.awayTeamGoals, 0);
      return result;
    }
    const result = matches.reduce((acc, match) => acc + match.homeTeamGoals, 0);
    return result;
  }

  public static resultGoalsBalance(matches: matchesAtributes[], endPoint: string) {
    const result = this.sumGoalsFavor(matches, endPoint) - this.sumGoalsOwn(matches, endPoint);
    return result;
  }

  public static resultEfficiency(matches: matchesAtributes[], endPoint: string) {
    const result = (this.pointsResult(matches, endPoint) / ((matches.length) * 3)) * 100;
    return Number(result.toFixed(2));
  }

  public static async resultSort(param: string | leaderBoard[]): Promise<leaderBoard[]> {
    const origin = typeof param === 'string' ? await this.findAllLeaderBoard(param)
      : param;
    const sortArray = origin.sort((a, b) => {
      if (a.totalPoints !== b.totalPoints) {
        return b.totalPoints - a.totalPoints;
      } if (a.totalVictories !== b.totalVictories) {
        return b.totalVictories - a.totalVictories;
      } if (a.goalsBalance !== b.goalsBalance) {
        return b.goalsBalance - a.goalsBalance;
      }
      return b.goalsFavor - a.goalsFavor;
    });

    return sortArray;
  }
}
